// File: Sloths.sol

contract Sloths is ERC721A, Ownable {  
    using Address for address;
    using Strings for uint256;

    struct VaccinationStatus { 
        uint256 tokenId;
        bool vaccinated;
    }
    
    uint256 public MAX_NFT_WALLET = 2; 
    uint256 public constant MAX_NFT_TRX = 10;          
    uint256 public constant MAX_SUPPLY = 4444;
    uint256 public constant RESERVED_AMOUNT = 200;    

    bool public isRevealed = false;
    bool public vaccinationPhase = false;    
    bool public publicSalePhase = false;
    bool public allowListEnabled = true;
    bool public claimTokensEnabled = false;

    address private _vaccinationContract;
    address private _tokenContract;    
    address private _tokenWallet;    

    string private _baseTokenURI;
    string private _suffix = ".json";
    bytes32 private root;    

    mapping(address => uint256) private walletAdoptedAmount;
    mapping(uint256 => bool) private _vaccinatedSloths;   
    
    constructor() ERC721A("Casual Sloths","CSLOTHS") {        
        _safeMint(0x1EB1Ea9f19D14fEc432714f4db7aA2240864246C, 150);
        _safeMint(0x4fE440D856059f08e0301a5Edc40556e73e4A600, 25);
        _safeMint(0x1BC80b413562Bc3362f7e8d7431255d5D18441a7, 25);
    }

    function adopt(uint256 quantity, bytes32[] memory _proof) public {       
        uint256 totalSupply = totalSupply();    
        require(publicSalePhase, "Can't adopt");         
        require(quantity > 0, "Amount too small");
        require(quantity <= MAX_NFT_TRX, "Max 10 tokens per trx"); 
        require((totalSupply + quantity) <= MAX_SUPPLY, "Purchase exceeding max supply"); 
        require((walletAdoptedAmount[msg.sender] + quantity) <= MAX_NFT_WALLET, "Can't adopt more per wallet");        

        if(allowListEnabled) 
            require(MerkleProof.verify(_proof, root, keccak256(abi.encodePacked(msg.sender))), "Not allowed to mint");    

        walletAdoptedAmount[msg.sender] += quantity;
        _safeMint(msg.sender, quantity);  
    }

    function vaccinateSloths(uint256[] memory tokenIds) public {        
        require(vaccinationPhase, "Vaccination phase not started");
        require(tokenIds.length > 0, "Amount too small");
        require(tokenIds.length <= MAX_NFT_TRX, "Max 10 tokens per trx"); 

        for (uint256 i = 0; i < tokenIds.length; i++) {
            if(ownerOf(tokenIds[i]) != msg.sender || _vaccinatedSloths[tokenIds[i]] == true)
                revert("Invalid token passed");

            _vaccinatedSloths[tokenIds[i]] = true;
        }
        VaccinatedSloths(_vaccinationContract).vaccinate(msg.sender, tokenIds.length);         
    }

    function isVaccinated(uint256 skip, uint256 take) public view returns (VaccinationStatus[] memory) {
        require(take > 0, "Amount too small");        

        VaccinationStatus[] memory result = new VaccinationStatus[](take);
        for (uint256 i = skip; i < (skip + take); i++) {            
            result[i].tokenId = i;
            result[i].vaccinated = _vaccinatedSloths[i];
        }
        return result;        
    }   

    function togglePublicSalePhase() public onlyOwner {
        publicSalePhase = !publicSalePhase;
    }

    function toggleAllowlist() public onlyOwner {
        allowListEnabled = !allowListEnabled;
    }

    function toggleReveal() public onlyOwner {
        isRevealed = !isRevealed;
    }

    function toggleVaccinationPhase() public onlyOwner {
        vaccinationPhase = !vaccinationPhase;
    }

    function toggleClaimTokens() public onlyOwner {
        claimTokensEnabled = !claimTokensEnabled;
    }
    
    function withdraw() public onlyOwner {
        uint256 balance = address(this).balance;
        payable(msg.sender).transfer(balance);
    }    

    function setVaccinationContract(address _contract) public onlyOwner {
        _vaccinationContract = _contract;
    }

    function setTokenContract(address _contract, address _wallet) public onlyOwner {
        _tokenContract = _contract;
        _tokenWallet = _wallet;
    } 

    function setRoot(uint256 _root) public onlyOwner {
        root = bytes32(_root);
    } 

    function setMaxPerWallet(uint256 _max) public onlyOwner {
        MAX_NFT_WALLET = _max;
    } 
    
    function getBalance() public view onlyOwner returns (uint256){
        return address(this).balance;
    } 
	
    function setBaseURI(string memory baseURI_, string memory suffix) public onlyOwner {
        _baseTokenURI = baseURI_;
        _suffix = suffix;
    }

    function claimTokens() public {    
        require(claimTokensEnabled, "Cant claim tokens");

        uint256 tokens = _claim(msg.sender);
        SlothToken(_tokenContract).transferFrom(_tokenWallet, msg.sender, tokens);
    }

    function totalStake(address _owner) public view returns (uint256) {            
        return _totalStake(_owner);
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {  
        require(
            _exists(tokenId),
            "ERC721Metadata: URI query for nonexistent token"
        );      
        if(!isRevealed)
            return _baseTokenURI;
            
        return string(abi.encodePacked(_baseTokenURI, tokenId.toString(), _suffix));
    }
}
